    <script>
    document.addEventListener('DOMContentLoaded', function() {
        var burger = document.querySelector('.burger');
        if (burger) {
            burger.addEventListener('click', function() {
                if (window.innerWidth < 1100) {
                    document.body.classList.toggle('menuOpen');
                }
            if (document.body.classList.contains('menuOpen')) {
                document.body.style.overflow = 'hidden';
            } else {
                document.body.style.overflow = '';
            }
        });

        const onScreenElements = document.querySelectorAll('.onScreen');
        if ('IntersectionObserver' in window && onScreenElements.length) {
            const observer = new IntersectionObserver((entries, observer) => {
                entries.forEach(entry => {
                    if (entry.isIntersecting) {
                        entry.target.classList.add('active');
                        observer.unobserve(entry.target);
                    }
                });
            }, { threshold: 0.1 });
            onScreenElements.forEach(el => observer.observe(el));
        }

        var navLinks = document.querySelectorAll('header .container .main-navigation ul li a');
        navLinks.forEach(function(link) {
            link.addEventListener('click', function() {
            if (window.innerWidth < 1100) {
                document.body.classList.toggle('menuOpen');
                if (document.body.classList.contains('menuOpen')) {
                document.body.style.overflow = 'hidden';
                } else {
                document.body.style.overflow = '';
                }
            }
            });
        });
        }

        // sticky header
        let lastScrollPosition = window.scrollY;
        const header = document.querySelector('header');

        window.addEventListener('scroll', () => {
            const currentScroll = window.scrollY;
            
            if (currentScroll > 0) {
                header.classList.add('sticky');
            } else {
                header.classList.remove('sticky');
            }
            
            lastScrollPosition = currentScroll;
        });
    });
    </script>
